import{_ as t,c as o,a4 as s,o as i}from"./chunks/framework.C527evlL.js";const _=JSON.parse('{"title":"微信报表","description":"","frontmatter":{},"headers":[],"relativePath":"线上平台/微信平台/微信报表.md","filePath":"线上平台/微信平台/微信报表.md","lastUpdated":1722328722000}'),l={name:"线上平台/微信平台/微信报表.md"};function e(r,a,n,c,h,d){return i(),o("div",null,a[0]||(a[0]=[s('<h1 id="微信报表" tabindex="-1">微信报表 <a class="header-anchor" href="#微信报表" aria-label="Permalink to &quot;微信报表&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">提示</p><p>系统提供了线上查看营业数据的方式，可以查看每日的营业数据和目前店内的实时数据。</p></div><h3 id="一、权限设置" tabindex="-1">一、权限设置 <a class="header-anchor" href="#一、权限设置" aria-label="Permalink to &quot;一、权限设置&quot;">​</a></h3><p>首先，需要给相应的职位勾选权限，你可以到<strong>员工管理</strong>页面选中该员工，然后点击右下方的<strong>权限</strong>按钮，进入权限设置界面，勾选<strong>微管家</strong>的权限，如下图：</p><p><img src="https://wiki-cdsoft.oss-cn-hangzhou.aliyuncs.com/20240730160202.png" alt="20240730160202" loading="lazy"></p><h3 id="二、打开小程序" tabindex="-1">二、打开小程序 <a class="header-anchor" href="#二、打开小程序" aria-label="Permalink to &quot;二、打开小程序&quot;">​</a></h3><ol><li><p>在<strong>微信搜索框</strong>中搜索：<strong>成大微管家</strong><img src="https://wiki-cdsoft.oss-cn-hangzhou.aliyuncs.com/29A88A9D-0EE9-49CB-9D29-F023D96215E3_1_201_a.jpeg" alt="29A88A9D-0EE9-49CB-9D29-F023D96215E3_1_201_a" style="max-width:300px;height:auto;border:2px solid black;" loading="lazy"></p></li><li><p>打开小程序后会需要你填写门店和账号信息 <img src="https://wiki-cdsoft.oss-cn-hangzhou.aliyuncs.com/C855A43E-BFC6-4521-B4F0-63F59345161F_1_101_o.jpeg" alt="C855A43E-BFC6-4521-B4F0-63F59345161F_1_101_o" style="max-width:300px;height:auto;border:2px solid black;" loading="lazy"></p></li></ol><ul><li>门店号码：如果你不知道，可以咨询售后服务人员</li><li>员工号：就是上面你设置的员工的编号</li><li>密码：就是员工的密码，可以到<strong>员工管理</strong>里面设置一个</li></ul><p>填写完成后点击登录即可，登录成功后，就可以在小程序里面查看数据了。</p><h3 id="三、报表查看" tabindex="-1">三、报表查看 <a class="header-anchor" href="#三、报表查看" aria-label="Permalink to &quot;三、报表查看&quot;">​</a></h3><h4 id="_1-实时数据" tabindex="-1">1.实时数据 <a class="header-anchor" href="#_1-实时数据" aria-label="Permalink to &quot;1.实时数据&quot;">​</a></h4><p>登录成功后首先显示的就是实时数据，可以查看自总交班之后，到目前的店内营业情况。 <img src="https://wiki-cdsoft.oss-cn-hangzhou.aliyuncs.com/5494043C-A11D-4660-A26A-D76906817E48_1_101_o.jpeg" alt="5494043C-A11D-4660-A26A-D76906817E48_1_101_o" style="max-width:300px;height:auto;border:2px solid black;" loading="lazy"></p><h4 id="_2-营业日报" tabindex="-1">2.营业日报 <a class="header-anchor" href="#_2-营业日报" aria-label="Permalink to &quot;2.营业日报&quot;">​</a></h4><p>点击<strong>营业分析</strong>菜单，然后点击<strong>营业日报</strong>即可进入日报查看界面。 <img src="https://wiki-cdsoft.oss-cn-hangzhou.aliyuncs.com/E723160B-1D27-444A-BF2C-CBB74201EE02_1_101_o.jpeg" alt="E723160B-1D27-444A-BF2C-CBB74201EE02_1_101_o" style="max-width:300px;height:auto;border:2px solid black;" loading="lazy"></p><p><img src="https://wiki-cdsoft.oss-cn-hangzhou.aliyuncs.com/CA176536-3F92-4630-897E-AE6EF7F1AE72_1_101_o.jpeg" alt="CA176536-3F92-4630-897E-AE6EF7F1AE72_1_101_o" style="max-width:300px;height:auto;border:2px solid black;" loading="lazy"></p>',15)]))}const g=t(l,[["render",e]]);export{_ as __pageData,g as default};
