import{_ as t,c as o,a2 as r,o as n}from"./chunks/framework.CiG9Bwkw.js";const p=JSON.parse('{"title":"如何续充会员","description":"","frontmatter":{},"headers":[],"relativePath":"营业前台/会籍管理/如何续充会员.md","filePath":"营业前台/会籍管理/如何续充会员.md","lastUpdated":1708937455000}'),i={name:"营业前台/会籍管理/如何续充会员.md"};function s(e,a,l,g,c,d){return n(),o("div",null,a[0]||(a[0]=[r('<h1 id="如何续充会员" tabindex="-1">如何续充会员 <a class="header-anchor" href="#如何续充会员" aria-label="Permalink to &quot;如何续充会员&quot;">​</a></h1><h2 id="一、查询会员" tabindex="-1">一、查询会员 <a class="header-anchor" href="#一、查询会员" aria-label="Permalink to &quot;一、查询会员&quot;">​</a></h2><p><img src="https://wiki-cdsoft.oss-cn-hangzhou.aliyuncs.com/20241010124131.png" alt="20241010124131" loading="lazy"> 点击<strong>会员管理</strong>，通过相应的条件查询出相应的会员。</p><ul><li>如果是实体卡，可以直接在<strong>芯片号</strong>内刷卡</li><li>如果是实体卡但是没有带卡，可以在<strong>会员卡号</strong>处输入卡号查询，也可以在<strong>手机号码</strong>处输入手机号查询</li><li>如果是手机号虚拟卡，那么可以直接在<strong>手机号码</strong>处输入手机号查询</li></ul><h2 id="二、确定充值金额" tabindex="-1">二、确定充值金额 <a class="header-anchor" href="#二、确定充值金额" aria-label="Permalink to &quot;二、确定充值金额&quot;">​</a></h2><p>选择查询出来的会员，点击上方的<strong>充值</strong>按钮，跳出充值界面<br><img src="https://wiki-cdsoft.oss-cn-hangzhou.aliyuncs.com/202401301802774.png" alt="image.png" loading="lazy"><br>通常来说，会员卡所属的卡种都会设置一个充值方案，充值本金和赠金都会直接显示出来</p><ul><li>如果你是按照卡种设置的金额续充，你现在可以直接点<strong>确认</strong>进入结账界面</li><li>如果客人要求的充值金额跟卡种设置的充值方案不一样，你可以修改<strong>充值本金</strong>和<strong>充值赠金</strong>，然后点击<strong>确认</strong>进入结账界面。</li></ul><p>不管是按照原方案充值，还是自己修改充值金额，都会在系统内生成充值记录。</p><h2 id="三、付款结账" tabindex="-1">三、付款结账 <a class="header-anchor" href="#三、付款结账" aria-label="Permalink to &quot;三、付款结账&quot;">​</a></h2><p>上一部中点击<strong>确认</strong>后都会进入结账界面，按照客人的支付方式结账即可。<br>请注意，一定要完成付款结账，否则续充是不完成的，也就是客人的会员卡里面是没有冲进去余额的。<br></p><p><img src="https://wiki-cdsoft.oss-cn-hangzhou.aliyuncs.com/1701592266600-6e2a52c2-fa4d-4221-b34a-2f13a13d428c.png%23averageHue%3D%2523dbc588%26clientId%3Duc6390cdb-afec-4%26from%3Dpaste%26height%3D1030%26id%3Du97e630ba%26originHeight%3D1030%26originWidth%3D1915%26originalType%3Dbinary%26ratio%3D1-20240130175835539.png" alt="image.png" loading="lazy"></p>',11)]))}const u=t(i,[["render",s]]);export{p as __pageData,u as default};
